import React from 'react';

const stats = [
  { id: 1, name: '登録企業数', value: '1,000+' },
  { id: 2, name: 'テンプレート数', value: '500+' },
  { id: 3, name: '月間利用者数', value: '10,000+' },
  { id: 4, name: '研修実施回数', value: '50,000+' },
];

const StatsSection = () => {
  return (
    <div className="bg-gray-50 py-24 sm:py-32">
      <div className="mx-auto max-w-7xl px-6 lg:px-8">
        <div className="mx-auto max-w-2xl lg:max-w-none">
          <div className="text-center">
            <h2 className="text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl">
              多くの企業に選ばれています
            </h2>
            <p className="mt-4 text-lg leading-8 text-gray-600">
              研修一括つくるくんは、多くの企業で活用されている研修テンプレートプラットフォームです
            </p>
          </div>
          <dl className="mt-16 grid grid-cols-1 gap-0.5 overflow-hidden rounded-2xl text-center sm:grid-cols-2 lg:grid-cols-4">
            {stats.map((stat) => (
              <div key={stat.id} className="flex flex-col bg-white p-8">
                <dt className="text-sm font-semibold leading-6 text-gray-600">{stat.name}</dt>
                <dd className="order-first text-3xl font-semibold tracking-tight text-blue-600">
                  {stat.value}
                </dd>
              </div>
            ))}
          </dl>
        </div>
      </div>
    </div>
  );
};

export default StatsSection;
