import React from 'react';
import { Link } from 'react-router-dom';
import HeroSection from './HomePage/HeroSection';
import FeaturesSection from './HomePage/FeaturesSection';
import TemplatesPreview from './HomePage/TemplatesPreview';
import Testimonials from './HomePage/Testimonials';
import PricingSection from './HomePage/PricingSection';
import FAQSection from './HomePage/FAQSection';
import CTASection from './HomePage/CTASection';
import NewsSection from './HomePage/NewsSection';
import BenefitsSection from './HomePage/BenefitsSection';
import StatsSection from './HomePage/StatsSection';

const HomePage = () => {
  return (
    <div className="bg-white">
      <div className="relative isolate px-6 pt-14 lg:px-8">
        <div className="mx-auto max-w-2xl py-32 sm:py-48 lg:py-56">
          <div className="text-center">
            <h1 className="text-4xl font-bold tracking-tight text-gray-900 sm:text-6xl">
              研修テンプレートで、効率的な人材育成を
            </h1>
            <p className="mt-6 text-lg leading-8 text-gray-600">
              研修一括つくるくんは、高品質な研修テンプレートを提供するプラットフォームです。
              テンプレートを活用して、効率的な研修準備と効果的な人材育成を実現しましょう。
            </p>
            <div className="mt-10 flex items-center justify-center gap-x-6">
              <Link
                to="/templates"
                className="rounded-md bg-blue-600 px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:bg-blue-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-blue-600"
              >
                テンプレートを見る
              </Link>
              <Link
                to="/about"
                className="text-sm font-semibold leading-6 text-gray-900"
              >
                詳しく見る <span aria-hidden="true">→</span>
              </Link>
            </div>
          </div>
        </div>
      </div>
      <StatsSection />
      <FeaturesSection />
      <BenefitsSection />
      <TemplatesPreview />
      <Testimonials />
      <PricingSection />
      <NewsSection />
      <FAQSection />
      <CTASection />
    </div>
  );
};

export default HomePage;
