import React from 'react';
import { 
  BookOpen, 
  Clock, 
  Users, 
  BarChart2, 
  FileText, 
  Share2 
} from 'lucide-react';

const features = [
  {
    name: '豊富なテンプレート',
    description: '様々な業界・職種に対応した研修テンプレートを提供。新入社員研修から専門スキル研修まで幅広くカバー。',
    icon: BookOpen,
    color: 'text-blue-500'
  },
  {
    name: '時間の効率化',
    description: '研修プログラムの準備時間を大幅に削減。テンプレートをカスタマイズするだけで、すぐに研修を開始できます。',
    icon: Clock,
    color: 'text-green-500'
  },
  {
    name: 'チーム共有',
    description: '作成した研修プログラムをチーム全体で共有。教育の質を統一し、効率的な人材育成を実現。',
    icon: Users,
    color: 'text-purple-500'
  },
  {
    name: '進捗管理',
    description: '研修の進捗状況をリアルタイムで確認。受講者の理解度や達成度を把握し、効果的なフォローアップが可能。',
    icon: BarChart2,
    color: 'text-yellow-500'
  },
  {
    name: 'カスタマイズ可能',
    description: 'テンプレートを自由に編集・カスタマイズ。自社の文化やニーズに合わせた研修プログラムを作成。',
    icon: FileText,
    color: 'text-red-500'
  },
  {
    name: 'シンプルな共有',
    description: '作成した研修プログラムを簡単に共有。URL一つでアクセス可能なため、場所を問わず研修を実施できます。',
    icon: Share2,
    color: 'text-indigo-500'
  }
];

const FeaturesSection = () => {
  return (
    <div className="bg-white py-24 sm:py-32">
      <div className="mx-auto max-w-7xl px-6 lg:px-8">
        <div className="mx-auto max-w-2xl lg:text-center">
          <h2 className="text-base font-semibold leading-7 text-blue-600">効率的な研修管理</h2>
          <p className="mt-2 text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl">
            研修管理を簡単に、効果的に
          </p>
          <p className="mt-6 text-lg leading-8 text-gray-600">
            豊富なテンプレートと使いやすい機能で、研修の準備から実施、管理までを効率化します。
          </p>
        </div>
        <div className="mx-auto mt-16 max-w-2xl sm:mt-20 lg:mt-24 lg:max-w-none">
          <dl className="grid max-w-xl grid-cols-1 gap-x-8 gap-y-16 lg:max-w-none lg:grid-cols-3">
            {features.map((feature) => {
              const Icon = feature.icon;
              return (
                <div key={feature.name} className="flex flex-col">
                  <dt className="flex items-center gap-x-3 text-base font-semibold leading-7 text-gray-900">
                    <Icon className={`h-5 w-5 flex-none ${feature.color}`} aria-hidden="true" />
                    {feature.name}
                  </dt>
                  <dd className="mt-4 flex flex-auto flex-col text-base leading-7 text-gray-600">
                    <p className="flex-auto">{feature.description}</p>
                  </dd>
                </div>
              );
            })}
          </dl>
        </div>
      </div>
    </div>
  );
};

export default FeaturesSection;
